def findClosestValueInBst(tree, target):
    currValue = tree.value
    if currValue == target:
        return currValue
    elif target < currValue:
        if tree.left == None:
            return currValue
        else:
            findClosestValueInBst(tree.left, target)
    elif target <= currValue:
        if tree.right == None:
            return currValue
        else:
            findClosestValueInBst(tree.right, target)


